export interface CrudManager<A, D, I> {
    find(details: D): Promise<I | undefined>;
    findAll(details: D): Promise<I[] | undefined>;
    findById(id: A): Promise<I | undefined>;
    findOrCreate(details: D): Promise<I | undefined>;
    create(details: D): Promise<I | undefined>;
    modify(item: I): Promise<any>;
    destroy(item: I): Promise<any>;
}
